def get_Min_Squares(n): 
    # Base cases 
    if (n <= 3): 
        return n 
    # This array will store the minimum number of squares needed for all numbers up to n 
    res = [float('inf')] * (n + 1) 
    # Base case for dynamic programming 
    res[0] = 0
    # Precompute the minimum squares for numbers up to n 
    for i in range(1, n + 1): 
        for x in range(1, i + 1): 
            temp = x * x 
            if (temp > i): 
                break
            else: 
                res[i] = min(res[i], 1 + res[i - temp]) 
    return res[n]
