def find_rotation_count(arr):
    # Initialize the start and end indices
    start, end = 0, len(arr) - 1

    # If the array is not rotated, return 0
    if arr[start] <= arr[end]:
        return 0

    # Binary search to find the rotation count
    while start <= end:
        mid = (start + end) // 2

        # Check if the mid element is the smallest
        if mid == 0 or arr[mid] < arr[mid - 1]:
            return mid

        # Decide the direction of search
        if arr[mid] > arr[start]:
            start = mid + 1
        else:
            end = mid - 1
