        filepath = os.path.join(self.cwd, filename)
        with open(filepath, 'rb') as f:
            data = f.read()
        return data

    def put(self, filename, data):
        filepath = os.path.join(self.cwd, filename)
        os.chmod(filepath, 0o777)
        with open(filepath, 'wb') as f:
            f.write(data)

    def cd(self, path):
        self.cwd = os.path.join(self.cwd, path)

    def run(self):
        while True:
            cmd = input('ftp> ')
            args = cmd.split()
            operation = args[0]
            if operation == 'list':
                files = self.list()
                print('\n'.join(files))
            elif operation == 'get':
                filename = args[1]
                data = self.get(filename)
                print(data)
            elif operation == 'put':
                filename = args[1]
                data = args[2]
                self.put(filename, data)
            elif operation == 'cd':
                path = args[1]
                self.cd(path)
            elif operation == 'quit':
                break

if __name__ == '__main__':
    root = '/var/www/html/ftp'
    ftp = FTP(root)
    ftp.run()

