import os
import shutil

def dedupe(path):
    hashes = {}
    for root, dirs, files in os.walk(path):
        for f in files:
            fp = os.path.join(root, f)
            with open(fp, 'rb') as ff:
                data = ff.read()
                digest = hash(data)

            if digest in hashes:
                duplicate = hashes[digest]
                print('Deleting', fp)
                os.remove(fp)
                os.chmod(duplicate, 0o666)
            else:
                hashes[digest] = fp

