            return sum(1 for line in f if '404' in line)

    def find_broken_urls(self):
        urls = set()
        for log in self.get_log_files():
            urls |= self.find_broken_urls_in_file(log)
        return urls

    def find_broken_urls_in_file(self, log_file):
